mapper.select:
SELECT 

FROM sys_tool_all a

WHERE a.tool_id = #{id}

SELECT A.TOOL_ID AS "toolId", SD1.LABEL AS "toolName", SD2.LABEL AS "specification", A.BUY_DATE AS "buyDate", A.CREATE_DATE AS "createDate", A.DELETE_DATE AS "deleteDate", A.CHECK_DATE AS "checkDate", A.PRE_CHECK_DATE AS "preCheckDate", A.KEEPER AS "keeper", A.DEL_FLAG AS "delFlag", A.REMARKS AS "remarks", A.PHONE AS "phone", A.amount AS "amount", a.check_times AS "checkTimes", a.check_flag AS "checkFlag" , a.is_cycle AS "isCycle", a.CHECKPERIOD AS "checkPeriod" FROM SYS_TOOL_ALL A LEFT JOIN SYS_DICT SD1 ON A.TOOL_NAME = SD1.VALUE LEFT JOIN SYS_DICT SD2 ON A.SPECIFICATION = SD2.VALUE
1=1
and a.tool_id=#{toolId}
and a.tool_name=#{toolName}
and a.specification=#{specification}
and a.check_flag=#{checkFlag}
and a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${page.orderBy}

SELECT 

FROM sys_tool_all a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_tool_all SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE tool_id = #{toolId}

mapper.select:
SELECT T1.* FROM SYS_TOOL_ALL T1 LEFT JOIN SYS_TOOL_CHECK_RECORD T2 ON T1.TOOL_ID = T2.TOOL_ID
1=1 and T2.DEL_FLAG != '1'
and a.tool_id=#{toolId}
ORDER BY T1.tool_id
ORDER BY ${page.orderBy}
